$ts= Read-Host 'Enter Arbitary Value'get-FirstRepeatingcharecter $tsfunction get-FirstRepeatingcharecter{$chk=0$carr = '';foreach ($char in [char[]] $ts) {if($carr.length -gt 0){	if(($carr.ToCharArray()) -contains [char]$char)	{$chk=1		write-host "First repeated character : $char"		break	}}$carr = $carr + $char; }if(!$chk){Write-Host "No repetetive charecter found"}}